    @objc public dynamic class func drawConnectingDiamondStationView(frame: CGRect = CGRect(x: 0, y: 0, width: 10, height: 10)) {
        //// Color Declarations
        let tripCanvasColor = UIColor(red: 0.078, green: 0.294, blue: 0.533, alpha: 1.000)

